    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Comentario index{% endblock %}

{% block body %}
    <table class=\"table\">
        <thead>
            <tr>
                <th>Id</th>
                <th>Fecha</th>
                <th>Texto</th>
                <th>Estado</th>
                <th>actions</th>
            </tr>
        </thead>
        <tbody>
        {% for comentario in comentarios %}
            <tr>
                <td>{{ comentario.id }}</td>
                <td>{{ comentario.fecha ? comentario.fecha|date('Y-m-d H:i:s') : '' }}</td>
                <td>{{ comentario.texto }}</td>
                <td>{{ comentario.estado }}</td>
                <td>
                    <a href=\"{{ path('app_admin_comentario_show', {'id': comentario.id}) }}\">show</a>
                    <a href=\"{{ path('app_admin_comentario_edit', {'id': comentario.id}) }}\">edit</a>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"5\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    <a href=\"{{ path('app_admin_comentario_new') }}\">Create new</a>
{% endblock %}
", "admin/comentario/index.html.twig", "/var/www/html/symfony/blog/templates/admin/comentario/index.html.twig");
